import { Component } from '@angular/core';
import { ProductService } from '../product.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.scss'],
})
export class ProductListComponent {
  categories: any = [];
  products: any = [];
  selectedProducts: any = [];

  constructor(
    private productService: ProductService,
    private router: Router
  ) {}

  ngOnInit() {
    this.getCategories();
    this.getProducts();
  }

  getCategories = () => {
    this.productService.getCategories().subscribe((response: any) => {
      this.categories = response;
    });
  };

  getProducts = () => {
    this.productService.getProducts().subscribe((response: any) => {
      this.products = response;
      this.selectedProducts = this.products;
    });
  };

  categoryChange = (data: any) => {
    console.log('data.target.value', data.target.value);
    this.selectedProducts = this.products.filter((el: any) => el.categoryId == data.target.value);
  };

  productDetails = (pid: any, cid: any) => {
    this.router.navigateByUrl(`productDetails/${pid}/${cid}`);
  };
}
<form #product="ngForm">
  <h1>Product Listing Page</h1>

  Category
  <select id="pCategory" name="pCategory" (change)="categoryChange($event)">
    <option *ngFor="let category of categories; let i = index" [value]="category.id">
      {{ category.name }}
    </option>
  </select>
<pre>{{selectedProducts | json}}</pre>
  <div name= *ngFor="let product of selectedProducts" [(ngModel)]="selectedProducts" >
  <div >
    <span (click)="productDetails(product.id, product.categoryId)"><img src="{{ product.imageUrl }}" /></span>
    <br />
    <span>{{ product.name }}</span>
  </div>
</div>
</form>
