import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ProductService } from '../product.service';

@Component({
  selector: 'app-product-details',
  templateUrl: './product-details.component.html',
  styleUrls: ['./product-details.component.scss']
})
export class ProductDetailsComponent {
  productId: any;
  productDetails: any;

  constructor(private route: ActivatedRoute, private productService: ProductService) {}

  ngOnInit() {
    this.route.params.subscribe(params => {
      this.productId = params['id']; // Access the 'id' parameter from the URL
      console.log('productId', this.productId);
      this.getProductData(this.productId);
    });
  }

  getProductData = (productId: any) => {
    this.productService.getProductDetails(productId).subscribe((response: any) => {
      console.log('Response', response);
      this.productDetails = response
    })
  }

}
<form>
  <h1>Products Details Page</h1>
  <div *ngFor="let product of productDetails">
    <div >
    <span>Product Image: <img src="{{ product?.imageUrl }}" /></span>
    <br />
    <span>Category: {{ product?.categoryId }}</span> <br />
    <span>Product Name: {{ product?.name }}</span> <br />
    <span>Product Description: {{ product?.description }}</span> <br />
  </div>
</div>
</form>
