import { Component } from '@angular/core';
import { ProductService } from '../product.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.scss'],
})
export class ProductListComponent {
  categories: any = [];
  products: any = [];
  selectedProducts: any = [];
  selected: boolean = false;
  defaultProduct: any;
  isChecked: Boolean = true
  constructor(
    private productService: ProductService,
    private router: Router
  ) {}

  ngOnInit() {
    this.getCategories();
    this.getProducts();
    this.selected = true;
  }

  categoryId: any;
  productCatId: any;
  category(index: any, item: any){
    this.categoryId = item.id; 
 }

 product(index: any, item: any){
  this.productCatId = item.categoryId;
  console.log('this.productCatId', this.productCatId);
  
}

  getCategories = () => {
    this.productService.getCategories().subscribe((response: any) => {
      this.categories = response;
      console.log(this.categories, 'this.categories')
    });
  };

  getProducts = () => {
    this.productService.getProducts().subscribe((response: any) => {
      this.products = response;
      console.log('this.products', this.products);
      if(this.categoryId === this.productCatId) {
        console.log('this.categoryId === this.productCatId', this.categoryId !== this.productCatId);
        this.selectedProducts = this.products
        console.log('this.selectedProducts', this.selectedProducts);
      }

    });
  };

  

  productDetails = (pid: any, cid: any) => {
    this.router.navigateByUrl(`productDetails/${pid}/${cid}`);
  };
  

categoryChange = (data: any, category: any) => {
  console.log('data.target.value', data.target.value);
  
  this.selectedProducts = this.products.filter((el: any) => el.categoryId == data.target.value);
};
}
<form>
  <h1>Product Listing Page</h1>

  Category
  <select id="pCategory" name="pCategory" (change)="categoryChange($event, categories)">
    <option *ngFor="let category of categories; trackBy: category; let i = index;" [value]="category.id"> {{category.id}}
      {{ category.name }}
    </option>
  </select>
<pre>{{selectedProducts | json}}</pre>
  <div *ngFor="let product of selectedProducts; trackBy: product; let i = index;" [(ngModel)]="selectedProducts" 
  [ngModelOptions]="{standalone: true}" ngDefaultControl>
  <div >
    <span (click)="productDetails(product.id, product.categoryId)"><img src="{{ product.imageUrl }}" /></span>
    <br />
    <span>{{ product.name }}</span>
  </div>
</div>
</form>
